package com.wiley.gr.ace.authorservices.model.external;

/**
 * The Class CDMAffiliation.
 */
public class CDMAffiliation {

    /** The institution id. */
    private String institutionId;

    /** The institution name. */
    private String institutionName;

    /** The department id. */
    private String departmentId;

    /** The department name. */
    private String departmentName;

    /** The city. */
    private String city;

    /** The country code. */
    private String countryCode;

    /** The country name. */
    private String countryName;

    /** The affiliation id. */
    private String affiliationId;

    /**
     * Gets the affiliation id.
     *
     * @return the affiliation id
     */
    public String getAffiliationId() {
        return affiliationId;
    }

    /**
     * Sets the affiliation id.
     *
     * @param affiliationId
     *            the new affiliation id
     */
    public void setAffiliationId(String affiliationId) {
        this.affiliationId = affiliationId;
    }

    /**
     * Gets the institution id.
     *
     * @return the institution id
     */
    public String getInstitutionId() {
        return institutionId;
    }

    /**
     * Sets the institution id.
     *
     * @param institutionId
     *            the new institution id
     */
    public void setInstitutionId(String institutionId) {
        this.institutionId = institutionId;
    }

    /**
     * Gets the institution name.
     *
     * @return the institution name
     */
    public String getInstitutionName() {
        return institutionName;
    }

    /**
     * Sets the institution name.
     *
     * @param institutionName
     *            the new institution name
     */
    public void setInstitutionName(String institutionName) {
        this.institutionName = institutionName;
    }

    /**
     * Gets the department id.
     *
     * @return the department id
     */
    public String getDepartmentId() {
        return departmentId;
    }

    /**
     * Sets the department id.
     *
     * @param departmentId
     *            the new department id
     */
    public void setDepartmentId(String departmentId) {
        this.departmentId = departmentId;
    }

    /**
     * Gets the department name.
     *
     * @return the department name
     */
    public String getDepartmentName() {
        return departmentName;
    }

    /**
     * Sets the department name.
     *
     * @param departmentName
     *            the new department name
     */
    public void setDepartmentName(String departmentName) {
        this.departmentName = departmentName;
    }

    /**
     * Gets the city.
     *
     * @return the city
     */
    public String getCity() {
        return city;
    }

    /**
     * Sets the city.
     *
     * @param city
     *            the new city
     */
    public void setCity(String city) {
        this.city = city;
    }

    /**
     * Gets the country code.
     *
     * @return the country code
     */
    public String getCountryCode() {
        return countryCode;
    }

    /**
     * Sets the country code.
     *
     * @param countryCode
     *            the new country code
     */
    public void setCountryCode(String countryCode) {
        this.countryCode = countryCode;
    }

    /**
     * Gets the country name.
     *
     * @return the country name
     */
    public String getCountryName() {
        return countryName;
    }

    /**
     * Sets the country name.
     *
     * @param countryName
     *            the new country name
     */
    public void setCountryName(String countryName) {
        this.countryName = countryName;
    }

}
