package com.wiley.gr.ace.authorservices.model.external;

/**
 * The Class CoAuthorElement.
 * 
 * @author virtusa version 1.0
 */
public class CoAuthorElement {

    /** The first name. */
    private String firstName;

    /** The user id. */
    private String userId;

    /** The institution. */
    private String institution;

    /** The email id. */
    private String emailId;

    /** The last name. */
    private String lastName;

    /** The department. */
    private String department;

    /**
     * Gets the first name.
     *
     * @return the first name
     */
    public final String getFirstName() {
        return firstName;
    }

    /**
     * Sets the first name.
     *
     * @param firstName
     *            the new first name
     */
    public final void setFirstName(final String firstName) {
        this.firstName = firstName;
    }

    /**
     * Gets the user id.
     *
     * @return the user id
     */
    public final String getUserId() {
        return userId;
    }

    /**
     * Sets the user id.
     *
     * @param userId
     *            the new user id
     */
    public final void setUserId(final String userId) {
        this.userId = userId;
    }

    /**
     * Gets the institution.
     *
     * @return the institution
     */
    public final String getInstitution() {
        return institution;
    }

    /**
     * Sets the institution.
     *
     * @param institution
     *            the new institution
     */
    public final void setInstitution(final String institution) {
        this.institution = institution;
    }

    /**
     * Gets the email id.
     *
     * @return the email id
     */
    public final String getEmailId() {
        return emailId;
    }

    /**
     * Sets the email id.
     *
     * @param emailId
     *            the new email id
     */
    public final void setEmailId(final String emailId) {
        this.emailId = emailId;
    }

    /**
     * Gets the last name.
     *
     * @return the last name
     */
    public final String getLastName() {
        return lastName;
    }

    /**
     * Sets the last name.
     *
     * @param lastName
     *            the new last name
     */
    public final void setLastName(final String lastName) {
        this.lastName = lastName;
    }

    /**
     * Gets the department.
     *
     * @return the department
     */
    public final String getDepartment() {
        return department;
    }

    /**
     * Sets the department.
     *
     * @param department
     *            the new department
     */
    public final void setDepartment(final String department) {
        this.department = department;
    }

    /**
     * Gets the phone no.
     *
     * @return the phone no
     */
    public final String getPhoneNo() {
        return phoneNo;
    }

    /**
     * Sets the phone no.
     *
     * @param phoneNo
     *            the new phone no
     */
    public final void setPhoneNo(final String phoneNo) {
        this.phoneNo = phoneNo;
    }

    /**
     * Gets the article id.
     *
     * @return the article id
     */
    public final String getArticleId() {
        return articleId;
    }

    /**
     * Sets the article id.
     *
     * @param articleId
     *            the new article id
     */
    public final void setArticleId(final String articleId) {
        this.articleId = articleId;
    }

    /** The phone no. */
    private String phoneNo;

    /** The article id. */
    private String articleId;

}
